thousandNum = ""
greatestVal = 0
adjacents = 13

for a in range(0, len(thousandNum) - adjacents + 1):
    val = 1 #Variable is set to 1 to be multiplied with the product

    for b in range(a, a + adjacents):
        val *= int(thousandNum[b: b + 1]) #After going through the list and finding the largest prouduct, makes val equal to that product

    if val > greatestVal:
        greatestVal = val #Makes greatestVal now the largest product
    
print("The greatest product is:")
print(greatestVal)